<?php

try {
    // Connexion ou création de la base de données SQLite
    $pdo = new PDO('sqlite:./storage/database.sqlite');
    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);

    $pdo->exec("CREATE TABLE IF NOT EXISTS sound (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        level INTEGER NOT NULL,
        date TEXT,
        time TEXT
    )");

    $pdo->exec("CREATE TABLE IF NOT EXISTS user (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        surname TEXT NOT NULL,
        name TEXT NOT NULL,
        pseudo TEXT UNIQUE NOT NULL,
        password TEXT NOT NULL,
        auth TEXT CHECK(auth IN ('admin', 'basic')) NOT NULL,
        picture TEXT NOT NULL
    )");

    $pdo->exec("CREATE TABLE IF NOT EXISTS message (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        type INTEGER NOT NULL,
        warning TEXT,
        color TEXT
    )");

    $pdo->exec("CREATE TABLE IF NOT EXISTS cursor (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        type INTEGER NOT NULL,
        value INTEGER
    )");

    // Vider le contenu des tables (sans supprimer les tables elles-mêmes)
    $tables = ['sound', 'user', 'message', 'cursor'];
    foreach ($tables as $table) {
        $pdo->exec("DELETE FROM $table");
    }

    // Insertion des données
    $pdo->exec("INSERT INTO sound (level, date, time) VALUES
        (28, '20/01/2025', '10:00:00'),
        (32, '20/01/2025', '10:01:00'),
        (44, '20/01/2025', '10:02:00'),
        (36, '20/01/2025', '10:03:00'),
        (45, '20/01/2025', '10:04:00'),
        (57, '20/01/2025', '10:05:00')");

    $pdo->exec("INSERT INTO message (type, warning, color) VALUES
        ('0', 'Bien !', '#B2FFC8'),
        ('1', 'Attention !', '#FFDF8C'),
        ('2', 'Trop Fort !', '#FF6666')");

    $pdo->exec("INSERT INTO cursor (type, value) VALUES
        ('0', 22),
        ('1', 64)");

    $pdo->exec("INSERT INTO user (surname, name, pseudo, password, auth, picture) VALUES
        ('Jean', 'Martin', 'jeanmartin', 'ab6512a76dee1c57aa10e9be4f44b459dec319a6e9eea31b5aa3a8be3964d5d1', 'basic', ''),
        ('Aline', 'Dubois', 'alinedubois', '58a968ac31623bb14e335460d3075a97d64cfedc17d8c58630353cb1a0ab25f1', 'admin', '')");

    echo "Base de données SQLite et données initiales créées avec succès.";

} catch (PDOException $e) {
    echo "Erreur : " . $e->getMessage();
}

?>


